library(hexbin)
heatmap_c_1 <- ggplot(df%>%filter(LengthOfStay=="1"), aes(bloodureanitro, hematocrit))+ geom_hex(bins=15) +ggtitle(" LoS = 1") + xlab("Blood Urea \nNitrogen (mg/dL)") + ylab("Hematocrit \n(g/dL)") + theme_bw(10) + xlim(0,100)
